import React from 'react';
import { Link } from 'react-router-dom';
import { useContextGlobal } from './utils/global.context';

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Navbar = () => {
  const {dispatch, state} = useContextGlobal(); 
  const theme = state.theme;

  const handlerChangeTheme = () => {
    if (theme) {
      dispatch(
        {type: 'light'
      })
    } else 
    dispatch(
      {type: 'dark'
    })
  }
  console.log(state.theme);

  return (
    <>
    <p className='nombre'> DH Odonto </p>
    <nav>

          <li className='inicio'><Link to="/"> Inicio </Link> </li>
          <li className='2'><Link to="/contact"> Contacto </Link></li>
          <li className='3'><Link to="/favoritos"> Favoritos </Link> </li>

          <li><button onClick={handlerChangeTheme}>Cambiar tema{
              state.theme === 'dark' || !state.theme ? <>üåí</> : <>‚òÄÔ∏è</>
            }</button></li>


  </nav>
    </>
  )
}

export default Navbar
