import { createContext, useContext, useState, useReducer, useEffect } from "react";

export const ContextGlobal = createContext(undefined);

const initialStateTema = {theme: localStorage.getItem('tema'), data: []};

const reducerTheme = (state, action) => {
  switch (action.type) {
    case 'light':
      return { tema: false };
    case 'dark':
      return {tema: true}
    default:
      return state;
  }
}
const ContextProvider = ({children}) => {
    const [state, dispatch] = useReducer(reducerTheme, initialStateTheme);

    const [odontologos, setOdontologos] = useState([]);

    const getOdontologos = async() => {
    const res = await fetch('https://jsonplaceholder.typicode.com/users')
    const data = await res.json()
      setOdontologos(data)
    }
    useEffect(()=> {  
      getOdontologos()
    }, [])

    useEffect(() => {
    const tipeTema = state.tema ? 'light' : 'dark';
    localStorage.setItem('theme', tipeTema);
    document.documentElement.setAttribute('data-base-theme', tipeTema)
    }, [state.tema])

    return (
        <ContextGlobal.Provider 
          value={{state, dispatch, odontologos, setOdontologos}}>
            {children}
        </ContextGlobal.Provider>
    )
}
export default ContextProvider;
export const useContextGlobal = () => useContext(ContextGlobal)
